import React from "react";
import axios from 'axios';
import moment from 'moment';
import ReactDatetime from "react-datetime";
import { Link } from "react-router-dom";

import {
    FormGroup,
    Form,
    Input,
    Row,
    Card,
    Button,
    Container,
    Col,
    InputGroupAddon,
    InputGroupText,
    InputGroup
} from "reactstrap";

import TernerasDestetadasHeader from "../../components/Headers/controlPartos.js";

class insertarControlParto extends React.Component {

    constructor() {
        super();
        this.state = {
            id_bovino:"",
            id_tipo:"",
            tipo:[],
            nombre:"",
            id_raza: "",
            razas:[],
            finca:"",
            fecha_parto:"",
            pesaje:"",
            observaciones:"",
            id_mama:"",
            id_papa:"",
            mamas: [],
            papas: [],
            id_usuario:"",
            encargado:[]
        }
        this.onInputChange = this.onInputChange.bind(this);
    }

    componentDidMount() {

        this.state.nombre = localStorage.getItem("nombre")
        this.state.id_mama = localStorage.getItem("chapeta")
        axios
        .get("http://vache-server.herokuapp.com/registroTipos/")
        .then(response => {
            console.log(response)
            this.setState({
                tipos: response.data.info
            });
            console.log("Registro tipos")
            console.log(this.state.control);
        })
        .catch(error => {
            console.log(error);
        });

        axios
            .get("http://vache-server.herokuapp.com/registroRazas/")
            .then(response => {
                console.log(response)
                this.setState({
                    razas: response.data.info
                });
                console.log("Registro razas")
                console.log(this.state.control);
            })
            .catch(error => {
                console.log(error);
            });

        axios
            .get("http://vache-server.herokuapp.com/bovinos/novillonaLactante/")
            .then(response => {
                console.log(response)
                this.setState({
                   mamas: response.data.info
                });
                console.log("Registro Hembras")
                console.log(this.state.control);
            })
            .catch(error => {
                console.log(error);
            });
        axios
        .get("http://vache-server.herokuapp.com/bovinos/tipo/2")
        .then(response => {
            console.log(response)
            this.setState({
                papas: response.data.info
            });
            console.log("Registro Machos")
            console.log(this.state.control);
        })
        .catch(error => {
            console.log(error);
        });

        axios
        .get("http://vache-server.herokuapp.com/usuarios/NombreId")
        .then(response => {
            console.log(response)
            this.setState({
                encargado: response.data.info
            });
            console.log("Registro Usuarios")
            console.log(this.state.control);
        })
        .catch(error => {
            console.log(error);
        });
    }

    onSubmit = async (e) => {


        e.preventDefault();


        await axios.post('http://vache-server.herokuapp.com/controlPartos', {
            id_bovino:this.state.id_bovino,
            TiposBovino: this.state.TiposBovino,
            nombre:this.state.nombre,
            id_raza:this.state.razas,
            finca:this.state.finca,
            fecha_parto:this.state.fecha_parto,
            pesaje: this.state.pesaje,
            observaciones: this.state.observaciones,
            id_mama: this.state.id_mama,
            id_papa: this.state.id_papa,
            id_usuario:this.state.id_usuario,
        }).then((response) => {
            console.log(response);
           
        });


        this.setState({
            id_bovino:"",
            id_tipo:"",
            tipo:[],
            nombre:"",
            id_raza: "",
            razas:[],
            finca:"",
            fecha_parto:"",
            pesaje:"",
            observaciones:"",
            id_mama:"",
            id_papa:"",
            mamas: [],
            papas: [],
            id_usuario:"",
            encargado:[]
        });
    };


    onInputChange(e) {
        const name = e.target.name;
        const value = e.target.value;
        this.setState({
            [name]: value
        })
    };

    render() {
        return (
            <>
                <TernerasDestetadasHeader />
                <br />
                <Container className="mt--7" fluid>
                    <Row>
                        <div className="col">
                            <Card className="shadow">
                                <br></br>
                                <Form onSubmit={this.onSubmit} className="text-center">
                                    <Row>
                                        <Col md="5">
                                            <FormGroup>
                                                <span> Nombre de bovino </span>
                                                <Input
                                                    className="form-control-alternative"
                                                    id="exampleFormControlInput1"
                                                    placeholder="Nombre de bovino"
                                                    type="text"
                                                    value={this.state.nombre}
                                                    name="nombre"
                                                    onChange={this.onInputChange}
                                                    required
                                                />
                                            </FormGroup>
                                        </Col>
                                        <Col md="5">
                                            <FormGroup>
                                                <span> Fecha Parto </span>
                                                <InputGroup className="input-group-alternative">
                                                    <InputGroupAddon addonType="prepend">
                                                        <InputGroupText>
                                                            <i className="ni ni-calendar-grid-58" />
                                                        </InputGroupText>
                                                    </InputGroupAddon>
                                                    <ReactDatetime
                                                        inputProps={{
                                                            placeholder: "Fecha Parto"
                                                        }}
                                                        dateFormat={'DD-MM-YYYY'}
                                                        timeFormat={false}
                                                        value={new Date(this.state.fecha_parto)}
                                                        onChange={e => this.setState({ fecha_parto: e })}
                                                        name="fecha_parto"
                                                    />
                                                </InputGroup>
                                            </FormGroup>
                                        </Col>
                                    </Row>
                                    <Row>
                                        <Col md="5">
                                                <span> Raza de la cria</span>
                                                <FormGroup>
                                                <Input
                                                    className="form-control-alternative"
                                                    id="exampleFormControlInput1"
                                                    placeholder="Sexo de la Cria"
                                                    type="select"
                                                    name="id_raza"
                                                    onChange={this.onInputChange}
                                                    required
                                                />
                                            </FormGroup>
                                        </Col>
                                        <Col md="5">
                                            <FormGroup>
                                                <span> Finca </span>
                                                <Input
                                                    className="form-control-alternative"
                                                    id="exampleFormControlInput1"
                                                    placeholder="Finca"
                                                    type="text"
                                                    value={this.state.finca}
                                                    name="finca"
                                                    onChange={this.onInputChange}
                                                    required
                                                />
                                            </FormGroup>
                                        </Col>
                                        <Col md="5">
                                            <FormGroup>
                                                <span> Pesaje</span>
                                                <Input
                                                    className="form-control-alternative"
                                                    id="exampleFormControlInput1"
                                                    placeholder="Peso de la Cria"
                                                    type="number"
                                                    value={this.state.pesaje}
                                                    name="pesaje"
                                                    onChange={this.onInputChange}
                                                    required
                                                />
                                            </FormGroup>
                                        </Col>
                                    </Row>
                                    <Row>
                                        <Col md="5">
                                            <span> Madre</span>
                                            <FormGroup>
                                            <Input
                                                    className="form-control-alternative"
                                                    id="exampleFormControlInput1"
                                                    type="select"
                                                    name="id_mama"
                                                    onChange={this.onInputChange}
                                                    required
                                                >
                                                     
                                                </Input>
                                            </FormGroup>
                                        </Col>
                                        <Col md="5">
                                            <span> Padre</span>
                                            <FormGroup>
                                                <Input
                                                    className="form-control-alternative"
                                                    id="exampleFormControlInput1"
                                                    type="select"
                                                    name="id_papa"
                                                    onChange={this.onInputChange}
                                                    required
                                                >
                                                     
                                                </Input>
                                            </FormGroup>
                                        </Col>
                                    </Row>
                                    <Row>
                                    <Col md="5">
                                            <span> Observaciones</span>
                                            <FormGroup>
                                            <Input
                                                    className="form-control-alternative"
                                                    id="exampleFormControlInput1"
                                                    placeholder="Observaciones del Parto"
                                                    type="text"
                                                    value={this.state.observaciones}
                                                    name="detalles"
                                                    onChange={this.onInputChange}
                                                    required
                                                />
                                            </FormGroup>
                                        </Col>
                                        <Col md="5">
                                            <span> Usuario</span>
                                            <FormGroup>
                                            <FormGroup>
                                                <Input
                                                    className="form-control-alternative"
                                                    id="exampleFormControlInput1"
                                                    type="select"
                                                    name="id_usuario"
                                                    onChange={this.onInputChange}
                                                    required
                                                >
                                                     
                                                </Input>
                                            </FormGroup>
                                            </FormGroup>
                                        </Col>
                                    </Row>
                                    <div className="text-center">
                                        <Button
                                            type="submit"
                                            className="btn-warning btn-icon mr-4"
                                            color="warning"

                                        >
                                            <i className="ni ni-fat-add" />
                                            <span className="btn-inner--text">Insertar</span>
                                        </Button>
                                    </div>
                                </Form>
                            </Card>
                        </div>
                    </Row>
                </Container>
            </>
        );
    }
}

export default insertarControlParto;
