import React from "react";
import axios from "axios";
import moment from 'moment';
import { Link } from "react-router-dom";

import {
  Button,
  Card,
  CardHeader,
  DropdownMenu,
  DropdownItem,
  UncontrolledDropdown,
  DropdownToggle,
  Media,
  Modal,
  Table,
  Container,
  Row,
} from "reactstrap";


import Medicine from "../../components/Headers/controlPartos";

export default class controlPartos extends React.Component {

  state = {
    listaControlPartos: [],
    controlPartos: {
      id_parto:"",
      id_bovino:"",
      Bovino:"",
      fecha_parto :"",
      pesaje:"",
      observaciones:"",
      TiposBovino:"",
      id_bovino_genealogico:"",
      id_usuario:""
    },
    notificationModal: false,
  }

  componentDidMount() {
    this.listarControlesParto();
    localStorage.setItem("edit", "");
  }


  listarControlesParto = () => {
    axios
      .get("http://vache-server.herokuapp.com/controlPartos")
      .then(response => {
        console.log(response)
        this.setState({
          listaControlPartos: response.data.info
        });
        console.log("Registro Control de partos")
        console.log(this.state.control);
      })
      .catch(error => {
        console.log(error);
      });
  }

  eliminarControlParto = async (id_parto) => {
    const res = await axios.delete('http://vache-server.herokuapp.com/controlParto' + id_parto);
    console.log(res);
    this.listarControlesParto();
  };

  toggleModal = state => {
    this.setState({
      [state]: !this.state[state]
    });
  };

  cargarInformacion = (controlParto) => {
    localStorage.setItem("id_parto", controlParto.id_pa);
    localStorage.setItem("id_bovino", controlParto.id_bovino);
    localStorage.setItem("Bovino", controlParto.Bovino);
    localStorage.setItem("fecha_parto", controlParto.fecha_parto);
    localStorage.setItem("pesaje", controlParto.pesaje);
    localStorage.setItem("observaciones", controlParto.observaciones);
    localStorage.setItem("TiposBovinos", controlParto.TiposBovino);
    localStorage.setItem("id_bovino_genealogico", controlParto.id_bovino_genealogico);
    localStorage.setItem("id_usuario", controlParto.id_usuario)

  }

  render() {
  return (
    <>
      <Medicine />
      {/* Page content */}
      <Container className="mt--7" fluid>
      <Row>
          <div className="col">
            <Card className="shadow">
            <CardHeader className="bg-transparent pb-5">
            <div className="text-right">
              <Button
                className="btn-info btn-icon mr-4"
                to="/admin/insertarControlesPrenez" tag={Link}
              >
                 <i className="ni ni-fat-add" />
                <span className="btn-inner--text">Añadir</span>
              </Button>           
            </div>
          </CardHeader>
              <Table className="align-items-center table-flush" responsive>
                <thead className="thead-light">
                  <tr>
                    <th scope="col">Nombre Vaca</th>
                    <th scope="col">Fecha Parto</th>
                    <th scope="col">Pesaje</th>
                    <th scope="col">Observaciones</th>
                    <th scope="col">Tipo de bovino</th>
                    <th scope="col">Acciones</th>
                    <th scope="col" />
                  </tr>
                </thead>
                <tbody>
                {this.state.listaControlPartos.map((controlParto, i) => {
                      return (
                        <tr>
                          <td>{controlParto.Bovino}</td>
                          <td>{moment(new Date(controlParto.fecha_parto)).format('DD-MM-YYYY')}</td>
                          <td>{controlParto.pesaje }</td>
                          <td>{controlParto.pesaje}</td>
                          <td>{controlParto.observaciones}</td>
                          <td>{controlParto.TiposBovino}</td>
                          <td className="text-right">
                            <UncontrolledDropdown>
                              <DropdownToggle
                                className="btn-icon-only text-light"
                                role="button"
                                size="sm"
                                color=""
                                key={i} onClick={this.cargarInformacion.bind(this, controlParto)}
                              >
                                <i className="fas fa-ellipsis-v" />
                              </DropdownToggle>
                              <DropdownMenu className="dropdown-menu-arrow" right>

                                <DropdownItem
                                  href="/admin/actualizarcontrolPreñez/"
                                  key={i} onClick={this.cargarInformacion.bind(this, controlParto)}
                                >
                                  <i className="ni ni-ui-04" />
                            Actualizar
                          </DropdownItem>
                                
                                
                              </DropdownMenu>
                            </UncontrolledDropdown>
                          </td>
                        </tr>
                      );
                    })}
                </tbody>
              </Table>
            </Card>
          </div>
        </Row>
      </Container>
    </>
  );
};

}
